document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelector(".navbar-toggler"),t=document.querySelector(".navbar-collapse");e.addEventListener("click",(function(){t.classList.toggle("show")}));const o=document.querySelectorAll(".dropdown-toggle");o.forEach((e=>{e.addEventListener("click",(function(t){t.preventDefault(),t.stopPropagation(),o.forEach((t=>{t!==e&&t.parentElement.querySelector(".dropdown-menu").classList.remove("show")}));this.parentElement.querySelector(".dropdown-menu").classList.toggle("show")}))})),document.addEventListener("click",(function(e){e.target.closest(".dropdown")||document.querySelectorAll(".dropdown-menu.show").forEach((e=>{e.classList.remove("show")}))})),document.addEventListener("click",(function(e){!t.classList.contains("show")||e.target.closest(".navbar-collapse")||e.target.closest(".navbar-toggler")||t.classList.remove("show")}));document.querySelectorAll(".botao-colapsavel").forEach((e=>{e.addEventListener("click",(()=>{const t=e.dataset.target;document.getElementById(t).classList.toggle("colapsado"),e.classList.toggle("ativo")}))}))}));